import React, { useEffect, useState } from 'react';
import '../styles/HabitsGrid.css';
import Habit from './Habit';
import HabitForm from './HabitForm';

const ProgressBar = ({ progress }) => {

    return (
      <div className="progress-bar">
        <div
          className="progress-bar-fill"
          style={{ width: `${progress}%` }}
        ></div>
      </div>
    );
  };

const HabitsGrid = () => {

  const [habits, setHabits] = useState([
    { title: 'Read Book', description: 'Complete today to have the first streak', status: true },
    { title: 'Learning French', description: 'Complete today to have the first streak', status: false },
    { title: 'Morning Run', description: 'Complete today to have the first streak', status: false },
  ]);

  const [progress, setProgress] = useState(0); // Initial progress value

  const updateProgress = () => {
    const completedHabitsCount = habits.filter((habit) => habit.status === true).length;
    const totalHabitsCount = habits.length;
    const newProgress = Math.round((completedHabitsCount / totalHabitsCount) * 100,1);
    setProgress(newProgress);
  };

  useEffect(() => {
    updateProgress();
  }, [habits]);

  const handleHabitStatusChange = (habitIndex) => {
    const updatedHabits = [...habits];
    updatedHabits[habitIndex].status = !updatedHabits[habitIndex].status;
    setHabits(updatedHabits);
  };

  const [showForm, setShowForm] = useState(false);

  const handleAddHabit = (newHabit) => {
    setHabits([...habits, newHabit]);
    setShowForm(false); // Hide the form after adding a habit
  };

  return(
      <div className="habits-grid">
          <div className="progress-content">
              <p> {progress !== 100 ? "You are almost done, go ahead!" : "Congratulations, you are done!"} </p>
              <p> {progress}% </p>
          </div>
          <ProgressBar progress={progress}/>
          <div className="habits">
            {habits.map((habit, index) => (
              <Habit
                key={index}
                title={habit.title}
                description={habit.description}
                status={habit.status}
                xp={habit.xp}
                onStatusChange={() => handleHabitStatusChange(index)}
              />
            ))}
            {showForm ? <HabitForm onAddHabit={handleAddHabit} /> : "" }
            <button onClick={() => setShowForm(true)}>Add Habit</button>
          </div>
      </div>
  )
}

export default HabitsGrid;
