import React, { useEffect, useState } from 'react';
import '../styles/HabitsGrid.css';
import Habit from './Habit';

const ProgressBar = ({ progress }) => {

    return (
      <div className="progress-bar">
        <div
          className="progress-bar-fill"
          style={{ width: `${progress}%` }}
        ></div>
      </div>
    );
  };

const HabitsGrid = () => {

  const [habits, setHabits] = useState([
    { title: 'Read Book', description: 'Complete today to have the first streak', status: false },
    { title: 'Learning French', description: '1-day streak', status: false },
    { title: 'Morning Run', description: '1-day streak', status: false },
  ]);

    const [progress, setProgress] = useState(0); // Initial progress value

    return(
        <div className="habits-grid">
            <div className="progress-content">
                <p> You are almost done, go ahead! </p>
                <p> {progress}% </p>
            </div>
            <ProgressBar progress={progress}/>
            <div className="habits">
            {habits.map((habit, index) => (
              <Habit
                key={index}
                title={habit.title}
                description={habit.description}
                status={habit.status}
                onStatusChange={() => handleHabitStatusChange(index)}
              />
            ))}
            </div>
        </div>
    )
}

export default HabitsGrid;
