import React, { useState } from "react";

const HabitsContext = React.createContext();

const HabitsProvider = ({ children }) => {
  const [habits, setHabits] = useState([
    // { title: 'Read Book', description: 'Complete today to have the first streak', status: true },
    // { title: 'Learning French', description: 'Complete today to have the first streak', status: false },
    // { title: 'Morning Run', description: 'Complete today to have the first streak', status: false },
  ]);

  const [selectedDate, setSelectedDate] = useState(new Date());

  return (
    <HabitsContext.Provider value={{ habits, setHabits, selectedDate, setSelectedDate }}>
      {children}
    </HabitsContext.Provider>
  );
};

export { HabitsContext, HabitsProvider };
