import React, { useState } from "react";
import '../styles/Habit.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck } from '@fortawesome/free-solid-svg-icons';


const Habit = ({ title, description, status }) => {

    const [clicked, setClicked] = useState(status)

    function toggleStatus() {
        setClicked((prevClicked) => !prevClicked)
    }

    return(
        <div className="habit">
            <div onClick={() => toggleStatus()} className={`habit__icon ${status ? 'active' : ''}`}>
                { status && <FontAwesomeIcon className="habit__icon_fa" icon={faCheck} /> }
            </div>
            <div className="habit__content">
                <h1 className="habit__content-h1">{title}</h1>
                <p className="habit__content-p">{description}</p>
            </div>
        </div>
    )
}

export default Habit
