import React from "react";
import '../styles/Habit.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck } from '@fortawesome/free-solid-svg-icons';


const Habit = ({ key, habit, onStatusChange }) => {

    const handleStatusChange = () => {
        onStatusChange(!habit.status); // Invert the status value and call the callback function
      };

    const handleClick = async () => {
        const response = await fetch("/api/habits/" + key, {
            method : "DELETE",
        })
        const json = await response.json()
    }

    return(
        <div className="habit">
            <div onClick={handleStatusChange} className={`habit__icon ${habit.status ? 'active' : ''}`}>
                { habit.status && <FontAwesomeIcon className="habit__icon_fa" icon={faCheck} /> }
            </div>
            <div className="habit__content">
                <h1 className="habit__content-h1">{habit.title}</h1>
                <p className="habit__content-p">{habit.status ? "1-day streak" : habit.description}</p>
            </div>
            <div className="habit__delete">delete</div>
        </div>
    )
}

export default Habit
