import React, { useState, useReducer } from "react";

const HabitsContext = React.createContext();

export const habitsReducer = (state, action) => {
  switch (action.type) {
    case 'SET_HABITS':
      return { 
        habits: action.payload 
      }
    case 'CREATE_HABITS':
      return { 
        habits: [action.payload, ...state.habits] 
      }
    case 'DELETE_HABITS':
      return { 
        habits: state.habits.filter(h => h._id !== action.payload._id) 
      }
    default:
      return state
  }
}

const HabitsProvider = ({ children }) => {
  const [habits, setHabits] = useState([]);

  const [selectedDate, setSelectedDate] = useState(new Date());

  return (
    <HabitsContext.Provider value={{ habits, setHabits, selectedDate, setSelectedDate }}>
      {children}
    </HabitsContext.Provider>
  );
};

export { HabitsContext, HabitsProvider };
