import React, { useEffect, useState, useContext } from 'react';
import '../styles/HabitsGrid.css';
import Habit from './Habit';
import ProgressBar from './ProgressBar';

const HabitsGrid = () => {

  const [habits, setHabits] = useState(null);

  const [selectedDate, setSelectedDate] = useState(new Date());

  const [progress, setProgress] = useState(0); // Initial progress value

  const updateProgress = () => {
    const completedHabitsCount = habits ? habits.filter((habit) => habit.status === true).length : 0;
    const totalHabitsCount = habits.length;
    const newProgress = Math.round((completedHabitsCount / totalHabitsCount) * 100,1);
    setProgress(newProgress);
  };

  useEffect(() => {
    const fetchHabits = async() => {
      const response = await fetch("/api/habits")
      const json = await response.json()

      if (response.ok) {
        setHabits(json)
      }
    }
    fetchHabits();
    updateProgress();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const filteredHabits = habits ? habits.filter(
    (habit) =>
      habit.date.toDateString() === selectedDate.toDateString()
  ) : "";

  const handleHabitStatusChange = (habitIndex) => {
    const updatedHabits = [...filteredHabits];
    updatedHabits[habitIndex].status = !updatedHabits[habitIndex].status;
    setHabits(updatedHabits);
  };

  return(
      <div className="habits-grid">
          <div className="progress-content">
              <p> {progress !== 100 ? "You are almost done, go ahead!" : "Congratulations, you are done!"} </p>
              <p> {progress ? progress : 0}% </p>
          </div>
          <ProgressBar progress={progress}/>
          <div className="habits">
            {filteredHabits.length !== 0 ? filteredHabits.map((habit, index) => (
              <Habit
                key={index}
                title={habit.title}
                description={habit.description}
                status={habit.status}
                onStatusChange={() => handleHabitStatusChange(index)}
              />
            )) : "No habits yet, you should add some!"}
          </div>
      </div>
  )
}

export default HabitsGrid;
