/* eslint-disable react-hooks/exhaustive-deps */
import { useEffect, useState } from 'react';
import '../styles/HabitsGrid.css';
import Habit from './HabitDetail';
import ProgressBar from './ProgressBar';
import { useHabitsContext } from '../hooks/useHabitsContext';
import { useAuthContext } from "../hooks/useAuthContext"
import { useDateContext } from "../hooks/useDateContext"

const HabitsGrid = () => {

  const { habits, dispatch } = useHabitsContext()
  const [ progress, setProgress ] = useState(0)
  const { user } = useAuthContext()
  const { selectedDate } = useDateContext() 
  const formatedDate = new Date(selectedDate).getTime()

  console.log("render")

  useEffect(() => {
    const fetchHabits = async() => {
      const response = await fetch("/api/habits", {
        headers: {'Authorization': `Bearer ${user.token}`},
      })
      const json = await response.json()

      if (response.ok) {
        dispatch({type: 'SET_HABITS', payload: json})
      }
    }

    if (user) {
      fetchHabits()
    }
  }, [dispatch, user])

  useEffect(() => {
    const falseHabitsCount = habits.length ? Math.round(
      (habits.filter(habit => habit.status === true).length / habits.length) * 100,
      1
    ) : 0;
    setProgress(falseHabitsCount);
    console.log(falseHabitsCount);
  }, [habits]);

  return(
      <div className="habits-grid">
          <ProgressBar progress={progress}/>
          <div className="habits">
          {habits.filter((habit) => habit.date === formatedDate).map((filteredHabit) => (
            <Habit key={filteredHabit._id} habit={filteredHabit} />
          ))}
          {habits.map(habit => {
            return(
              <>
                <p>{habit.date}</p>
                <p>{formatedDate}</p>
              </>
            )
          })}
          </div>
      </div>
  )
}

export default HabitsGrid;
