/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from 'react';
import '../styles/HabitsGrid.css';
import Habit from './HabitDetail';
import ProgressBar from './ProgressBar';
import { useHabitsContext } from '../hooks/useHabitsContext';

const HabitsGrid = () => {

  // eslint-disable-next-line no-unused-vars
  const { habits, dispatch } = useHabitsContext()
  const [ progress, setProgress ] = useState(0)

  useEffect(() => {
    const fetchHabits = async() => {
      const response = await fetch("/api/habits")
      const json = await response.json()

      if (response.ok) {
        dispatch({type: 'SET_HABITS', payload: json})
      }
    }
    fetchHabits()
  }, [dispatch]);

  useEffect(() => {
    const falseHabitsCount = habits.length ? Math.round(
      (habits.filter(habit => habit.status === true).length / habits.length) * 100,
      1
    ) : 0;
    setProgress(falseHabitsCount);
    console.log(falseHabitsCount);
  }, [habits]);

  return(
      <div className="habits-grid">
          <ProgressBar progress={progress}/>
          <div className="habits">
            {habits.map((habit) => (
              <Habit key={habit._id} habit = {habit}/>
            ))}
          </div>
      </div>
  )
}

export default HabitsGrid;
