import React from "react";
import '../styles/Habit.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck } from '@fortawesome/free-solid-svg-icons';


const Habit = ({ title, description, status, xp, onStatusChange }) => {

    const handleStatusChange = () => {
        onStatusChange(!status); // Invert the status value and call the callback function
      };

    return(
        <div className="habit">
            <div onClick={handleStatusChange} className={`habit__icon ${status ? 'active' : ''}`}>
                { status && <FontAwesomeIcon className="habit__icon_fa" icon={faCheck} /> }
            </div>
            <div className="habit__content">
                <h1 className="habit__content-h1">{title}</h1>
                <p className="habit__content-p">{status ? "1-day streak" : description}</p>
            </div>
            <div className="habit__xp">{xp}</div>
        </div>
    )
}

export default Habit
