import React from 'react';
import '../styles/DatesGrid.css'

const Square = ({ day, date, fullDate }) => {
  return (
    <div className="dates-grid__square">
      <div className="dates-grid__square__day-name">{day}</div>
      <div className="dates-grid__square__day-num">{date}</div>
      <div className="dates-grid__square__full-date">{fullDate}</div>
    </div>
  );
};

const DatesGrid = () => {
  const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const today = new Date();
  const dates = [];

  // Generate the next 6 dates
  for (let i = 0; i < 7; i++) {
    const date = new Date();
    const fullDate = date.strftime("%m/%d/%Y");
    date.setDate(today.getDate() + i);
    dates.push(date, fullDate);
  }

  return (
    <div className="dates-grid">
      {dates.map((date, index) => (
        <Square
          key={index}
          day={daysOfWeek[date.date.getDay()]}
          date={date.date.getDate()}
          fullDate = {date.fullDate}
        />
      ))}
    </div>
  );
};

export default DatesGrid;
