import { createContext, useReducer } from 'react'

export const HabitsContext = createContext()

export const habitsReducer = (state, action) => {
  console.log(action.payload)
  switch (action.type) {
    case 'SET_HABITS':
      return { 
        habits: action.payload 
      }
    case 'CREATE_HABIT':
      return { 
        habits: [action.payload, ...state.habits] 
      }
    case 'DELETE_HABIT':
      return { 
        habits: state.habits.filter(h => h._id !== action.payload._id) 
      }
    case 'UPDATE_HABIT':
      const updatedIndex = state.habits.findIndex(habit => habit._id === action.payload._id);
      const updatedHabits = [...state.habits];
      updatedHabits[updatedIndex] = action.payload;
      return {
        habits: updatedHabits,
      };
    default:
      return state
  }
}

export const HabitsContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(habitsReducer, { 
    habits: []
  })

  return (
    <HabitsContext.Provider value={{ ...state, dispatch }}>
      { children }
    </HabitsContext.Provider>
  )
}
