import React, { useEffect, useContext } from 'react';
import '../styles/HabitsGrid.css';
import Habit from './Habit';
import ProgressBar from './ProgressBar';
import { HabitsContext } from '../context/HabitsContext';

const HabitsGrid = () => {

  // eslint-disable-next-line no-unused-vars
  const { habits, dispatch } = HabitsContext()

  useEffect(() => {
    const fetchHabits = async() => {
      const response = await fetch("/api/habits")
      const json = await response.json()

      if (response.ok) {
        dispatch({type: 'SET_HABITS', payload: json})
      }
    }
    fetchHabits()
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [dispatch]);

  const handleHabitStatusChange = async (habitIndex) => {
    const updatedHabits = [...habits];
    updatedHabits[habitIndex].status = !updatedHabits[habitIndex].status;
    setHabits(updatedHabits);

    const habitToUpdate = updatedHabits[habitIndex];

    try {
      const response = await fetch(`/api/habits/${habitToUpdate._id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(habitToUpdate),
      });

      if (!response.ok) {
        throw new Error('Failed to update habit.');
      }
    } catch (error) {
      console.error(error);
      // Handle error scenario
    }
    console.log("updating")
  };

  return(
      <div className="habits-grid">
          <div className="habits">
            {habits.length !== 0 ? habits.map((habit, index) => (
              <Habit
                key={index}
                id = {habit._id}
                habit = {habit}
                onStatusChange={() => handleHabitStatusChange(index)}
              />
            )) : "No habits yet, you should add some!"}
          </div>
      </div>
  )
}

export default HabitsGrid;
